mapper.select:
SELECT 

FROM tcharge a

WHERE a.id = #{id}

SELECT 
	distinct , 
	IFNULL(mpt.pay_date, a.confirm_date) AS "maxPayDate"
FROM tcharge a


	a.del_flag = #{DEL_FLAG_NORMAL}



			    AND a.prj_num = #{project.prjNum}


			    AND a.prj_num in (

	               '${tempStr}'

	            )						




		AND b.prj_name like concat('%',#{project.prjName},'%') 


		AND b.build_corp_code = #{project.buildCorpCode}


		AND b.build_corp_name like concat('%',#{project.buildCorpName},'%') 


		AND pl.document_no like concat('%',#{project.projectLicense},'%') 


		AND a.report_staff = #{reportStaff.id}




			AND DATE_FORMAT(IFNULL(mpt.pay_date, a.confirm_date),'%Y-%m-%d') >= DATE_FORMAT(#{dateFrom},'%Y-%m-%d')


			AND DATE_FORMAT(IFNULL(mpt.pay_date, a.confirm_date),'%Y-%m-%d') <= DATE_FORMAT(#{dateTo},'%Y-%m-%d')




			AND DATE_FORMAT(a.report_date,'%Y-%m-%d') >= DATE_FORMAT(#{dateFrom},'%Y-%m-%d')


			AND DATE_FORMAT(a.report_date,'%Y-%m-%d') <= DATE_FORMAT(#{dateTo},'%Y-%m-%d')






			    AND a.status = #{status}


			    AND a.status in (

	               '${tempStr}'

	            )						






		ORDER BY ${page.orderBy}


		ORDER BY a.status, mpt.pay_date DESC


		ORDER BY a.status, a.id DESC

SELECT 

FROM tcharge a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

		SELECT 
			distinct , 
			IFNULL(mpt.pay_date, a.confirm_date) AS "maxPayDate"
		FROM tcharge a


			a.del_flag = #{DEL_FLAG_NORMAL}
			AND a.prj_num = #{project.prjNum}
			AND a.id < #{id}	

        ORDER BY a.id

mapper.update:
UPDATE tcharge SET
	report_date = #{reportDate},
	report_memo = #{reportMemo},
	status = #{status}
WHERE id = #{id}

UPDATE tcharge SET
	payment_code = #{paymentCode},
	virtual_bank_account = #{virtualBankAccount}

		,status = #{status}


WHERE id = #{id}

		UPDATE tcharge SET
			approve_staff = #{approveStaff.id},
			approve_date = #{approveDate},
            approve_memo = #{approveMemo},
			status = #{status}
		WHERE id = #{id}

update tcharge 
set land_pay_money = #{landPayMoney}
where id=#{id}

UPDATE tcharge SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
